mapper.select:
SELECT c.id AS classId, c.class_name AS className, c.leader_name AS leaderName, g.grade_name AS period, c.leader_name AS leaderName FROM base_class c INNER JOIN base_grade g ON c.grade_id = g.id INNER JOIN base_con_grade_user gu ON g.id = gu.grade_id where 1 = 1
AND c.grade_id = #{params.gradeId}
AND c.class_name LIKE CONCAT('%',#{params.className},'%')
AND c.leader_name LIKE CONCAT('%',#{params.teacher},'%')
AND gu.user_id = #{params.userId}
ORDER BY c.update_time DESC

SELECT COUNT(*) FROM base_class c INNER JOIN base_grade g ON c.grade_id = g.id INNER JOIN base_con_grade_user gu ON g.id = gu.grade_id where 1 = 1
AND c.grade_id = #{params.gradeId}
AND c.class_name LIKE CONCAT('%',#{params.className},'%')
AND c.leader_name LIKE CONCAT('%',#{params.teacher},'%')
AND gu.user_id = #{params.userId}

SELECT DISTINCT c.id AS classId, c.class_name AS className FROM base_class c INNER JOIN base_grade g ON c.grade_id = g.id INNER JOIN base_con_grade_user gu ON g.id = gu.grade_id WHERE 1 = 1
AND gu.grade_id = #{params.gradeId}
ORDER BY c.class_name DESC

SELECT DISTINCT c.period FROM base_class c
INNER JOIN base_con_class_user ur ON c.id = ur.class_id AND ur.user_id = #{userId}
LIMIT 7
