mapper.select:
SELECT s.id AS studentId, c.class_name AS className, s.name, s.sex, s.age, s.height, s.weight, s.phone, c.grade_id AS gradeId FROM base_student s JOIN base_class c ON s.class_id = c.id where 1 = 1
AND s.class_id = #{params.classId}
AND s.name LIKE CONCAT('%',#{params.name},'%')
AND s.sex = #{params.sex}
AND c.grade_id = #{params.gradeId}
ORDER BY s.update_time DESC

SELECT
COUNT(*)
FROM
base_student s
inner JOIN base_class c ON s.class_id = c.id
where 1 = 1

    AND s.class_id = #{params.classId}


    AND s.name LIKE CONCAT('%',#{params.name},'%')


    AND s.sex = #{params.sex}


    AND c.grade_id = #{params.gradeId}

SELECT s.id AS studentId, s.`name` AS studentName, c.class_name AS className, s.sex FROM base_student s INNER JOIN base_class c ON s.class_id = c.id INNER JOIN base_grade g ON c.grade_id = g.id
INNER JOIN football_base_team t ON g.id = t.grade_id
INNER JOIN football_base_team t ON c.id = t.class_id
where t.id = #{params.teamId}
AND s.sex = #{params.sex}
AND c.id = #{params.classId}
AND g.id = #{params.gradeId}
AND s.`name` LIKE CONCAT('%',#{params.studentName},'%')

SELECT s.id AS studentId, s.`name` AS studentName, bt.team_name AS teamName, g.grade_name AS gradeName, tp.score FROM football_con_team_players tp INNER JOIN football_base_team bt ON tp.team_id = bt.id INNER JOIN base_student s ON tp.student_id = s.id INNER JOIN base_grade g ON bt.grade_id = g.id
AND bt.grade_id = #{params.gradeId}
AND bt.team_type = #{params.teamType}
ORDER BY tp.score DESC, g.grade_name DESC

SELECT count(*) FROM football_con_team_players tp INNER JOIN football_base_team bt ON tp.team_id = bt.id INNER JOIN base_student s ON tp.student_id = s.id INNER JOIN base_grade g ON bt.grade_id = g.id
AND bt.grade_id = #{params.gradeId}
AND bt.team_type = #{params.teamType}

SELECT s.id AS studentId, s.`name` AS studentName, g.grade_name AS gradeName, c.class_name AS className, bt.team_name AS teamName, tp.score FROM football_con_team_players tp INNER JOIN football_base_team bt ON tp.team_id = bt.id INNER JOIN base_student s ON tp.student_id = s.id INNER JOIN base_grade g ON bt.grade_id = g.id INNER JOIN base_class c ON bt.class_id = c.id
AND bt.grade_id = #{params.gradeId}
AND bt.team_type = #{params.teamType}
AND bt.class_id = #{params.classId}
ORDER BY tp.score DESC, g.grade_name DESC

SELECT COUNT(*) FROM football_con_team_players tp INNER JOIN football_base_team bt ON tp.team_id = bt.id INNER JOIN base_student s ON tp.student_id = s.id INNER JOIN base_grade g ON bt.grade_id = g.id INNER JOIN base_class c ON bt.class_id = c.id
AND bt.grade_id = #{params.gradeId}
AND bt.team_type = #{params.teamType}
AND bt.class_id = #{params.classId}
ORDER BY tp.score DESC, g.grade_name DESC
