mapper.select:
SELECT t.id AS teamId, g.grade_name AS gradeName, c.class_name AS className, t.team_name AS teamName, t.team_type AS teamType, u.name AS creater, g.id AS gradeId, t.count FROM football_base_team t INNER JOIN base_class c ON t.class_id = c.id INNER JOIN base_grade g ON c.grade_id = g.id LEFT JOIN sys_user u ON t.user_id = u.id INNER JOIN base_con_grade_user gu ON g.id = gu.grade_id where 1 = 1
AND t.team_name LIKE CONCAT('%', #{params.teamName},'%')
AND t.team_type = #{params.teamType}
AND t.class_id = #{params.classId}
AND c.grade_id = #{params.gradeId}
AND gu.user_id = #{params.userId}
ORDER BY t.id DESC

SELECT t.id AS teamId, g.grade_name AS gradeName, t.team_name AS teamName, t.team_type AS teamType, u.name AS creater, g.id AS gradeId, t.count FROM football_base_team t INNER JOIN base_grade g ON t.grade_id = g.id LEFT JOIN sys_user u ON t.user_id = u.id INNER JOIN base_con_grade_user gu ON g.id = gu.grade_id WHERE 1 = 1 AND t.team_type = '1'
AND t.team_name LIKE CONCAT('%', #{params.teamName},'%')
AND t.team_type = #{params.teamType}
AND t.grade_id = #{params.gradeId}
AND gu.user_id = #{params.userId}
ORDER BY t.id DESC

SELECT COUNT(*) FROM football_base_team t INNER JOIN base_class c ON t.class_id = c.id INNER JOIN base_grade g ON c.grade_id = g.id LEFT JOIN sys_user u ON t.user_id = u.id INNER JOIN base_con_grade_user gu ON g.id = gu.grade_id where 1 = 1
AND t.team_name LIKE CONCAT('%', #{params.teamName},'%')
AND t.team_type = #{params.teamType}
AND t.class_id = #{params.classId}
AND c.grade_id = #{params.gradeId}
AND gu.user_id = #{params.userId}

SELECT COUNT(*) FROM football_base_team t INNER JOIN base_grade g ON t.grade_id = g.id LEFT JOIN sys_user u ON t.user_id = u.id INNER JOIN base_con_grade_user gu ON g.id = gu.grade_id WHERE 1 = 1 AND t.team_type = '1'
AND t.team_name LIKE CONCAT('%', #{params.teamName},'%')
AND t.team_type = #{params.teamType}
AND t.grade_id = #{params.gradeId}
AND gu.user_id = #{params.userId}

SELECT t.id AS teamId, t.team_name AS teamName, t.class_id AS classId, t.grade_id AS gradeId FROM football_base_team t WHERE 1 = 1
AND t.class_id = #{params.classId} AND t.team_type = 3
AND t.id != #{params.team1Id}

SELECT
    temp.teamId
FROM
    ( SELECT t.id AS teamId FROM football_base_team t
          WHERE t.id != #{params.team1Id}

            AND  t.period = #{params.period}


              AND  t.period = #{params.period}


              AND t.class_id = #{params.classId}

    ) AS temp
    INNER JOIN football_base_event e ON temp.teamId = e.team2_id
WHERE
    e.team1_id = #{params.team1Id}
    AND type = #{params.type}
    AND e.status in(0, 1)
UNION
SELECT
    temp.teamId
FROM
( SELECT t.id AS teamId FROM football_base_team t
        WHERE t.id != #{params.team1Id}

            AND  t.period = #{params.period}


            AND  t.period = #{params.period}


            AND t.class_id = #{params.classId}

    ) AS temp
    INNER JOIN football_base_event e ON temp.teamId = e.team1_id
WHERE
    e.team2_id = #{params.team1Id}
    AND type = #{params.type}
    AND e.status in(0, 1)

SELECT t.id AS teamId, t.team_name AS teamName, g.grade_name AS gradeName, c.class_name AS className, t.score FROM football_base_team t INNER JOIN base_class c ON t.class_id = c.id INNER JOIN base_grade g ON c.grade_id = g.id WHERE t.team_type = '3'
AND t.class_id = #{params.classId}
ORDER BY t.score DESC

SELECT t.id AS teamId, t.team_name AS teamName, g.grade_name AS gradeName, t.score FROM football_base_team t INNER JOIN base_grade g ON t.grade_id = g.id WHERE t.team_type = '1'
AND t.grade_id = #{params.gradeId}
ORDER BY t.score DESC

update.foreach:
UPDATE football_base_team

    score = score + #{bean.score}, update_time = CURRENT_TIMESTAMP


    id= #{bean.id}

mapper.select:
SELECT
    t.id AS teamId,
    t.team_name AS teamName
FROM
    football_base_team t
WHERE t.team_type = #{params.teamType}

    AND t.grade_id = #{params.gradeId}


    AND t.class_id = #{params.classId}

SELECT
  count(*)
FROM
football_base_team t
WHERE t.team_type = #{params.teamType}

    AND t.grade_id = #{params.gradeId}


    AND t.class_id = #{params.classId}
